        [TestMethod]
        public void ShouldIsAddress()
        {
            Assert.IsTrue(InputValidator.IsAddress(TEST_ADDRESS_WITH_CHECKSUM));
        }

        [TestMethod]
        public void ShouldCheckAddress()
        {
            Assert.IsTrue(InputValidator.CheckAddress(TEST_ADDRESS_WITH_CHECKSUM));
        }

        [TestMethod]
        public void ShouldIsTrytes()
        {
            Assert.IsTrue(InputValidator.IsTrytes(TEST_TRYTES, TEST_TRYTES.Length));
        }

        [TestMethod]
        public void ShouldIsValue()
        {
            Assert.IsTrue(InputValidator.IsValue("1234"));
        }

        [TestMethod]
        public void ShouldIsArrayOfHashes()
        {
            Assert.IsTrue(InputValidator.IsArrayOfHashes(new[] {TEST_HASH, TEST_HASH}));
        }

        [TestMethod]
        public void ShouldIsArrayOfTrytes()
        {
            Assert.IsTrue(InputValidator.IsArrayOfTrytes(new[] {TEST_TRYTES, TEST_TRYTES}));
        }

        [TestMethod]
        public void ShouldInvalidTxTrytes()
        {
            // We fail on having value above max supply
            Assert.IsFalse(InputValidator.IsArrayOfRawTransactionTrytes(new[] {TEST_TRYTES, TEST_TRYTES}));
        }

        [TestMethod]
        public void ShouldIsNinesTrytes()
        {
            Assert.IsTrue(InputValidator.IsNinesTrytes("999999999", 9));
        }

        [TestMethod]
        public void ShouldIsValidTransfer()
        {
            var transfer = new Transfer(TEST_ADDRESS_WITH_CHECKSUM, 0, TEST_MESSAGE, TEST_TAG);
            Assert.IsTrue(InputValidator.IsValidTransfer(transfer));
        }

        [TestMethod]
        public void ShouldIsTransfersCollectionValid()
        {
            List<Transfer> transfers = new List<Transfer>
            {
                new Transfer(TEST_ADDRESS_WITH_CHECKSUM, 0, TEST_MESSAGE, TEST_TAG),
                new Transfer(TEST_ADDRESS_WITH_CHECKSUM, 0),
                new Transfer(TEST_ADDRESS_WITH_CHECKSUM, 0)
            };

            Assert.IsTrue(InputValidator.IsValidTransfersCollection(transfers));
        }
    }
